	try {
		const config = {
			header: {
				'content-type': 'application/json',
			},
		};
		const body = {
			name,
			company,
			email,
		};

		await axios.post(`${process.env.REACT_APP_API_URL}/api/employers/register`, body, config);
	} catch (err) {
		if (err.response) {
			dispatch(applyError(err.response.data.message));
		} else {
			dispatch(applyError(err.message));
		}
	}
};

